import React,{useState} from 'react'
import CoinImage from './CoinImage'
import CoinName from './CoinName'
import CoinDetailInfo from './CoinDetailInfo';
import CoinPriceInfo from './CoinPriceInfo';

import './Coin.css'

const returnPriceData=()=>{
    var priceData = [];

    while(priceData.length < 18){
        var r = Math.floor(Math.random() * 10000) + 1;
        priceData.push(r);
    }
    console.log(priceData)

    const data = {
        labels: labels,
        datasets: [{
            label: 'Ethereum',
            backgroundColor: 'rgb(0, 128, 0)',
            borderColor: 'rgb(0, 128, 0)',
            data: priceData,
        }]
    }
    return data;
};

const labels = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December',
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
];


function Coin({ImgURL,CoinTitle,coinDetails,coinPrice}){

    const [priceData,setPriceData]=useState(returnPriceData()); 

    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage ImgURL={ImgURL}/>
                <CoinName CoinTitle={CoinTitle}/> 
            </div>
            <CoinDetailInfo coinDetails={coinDetails}/>
            <CoinPriceInfo coinPrice={coinPrice} data={priceData}/>
            <button type="button" className='button' onClick={()=>{
                setPriceData(returnPriceData());
            }}> Refresh </button>
        </div>
    );
}

export default Coin;
