import React from 'react'
import CoinImage from './CoinImage'
import CoinName from './CoinName'
import CoinDetailInfo from './CoinDetailInfo';
import CoinPriceInfo from './CoinPriceInfo';

import './Coin.css'

function Coin({ImgURL,CoinTitle,coinDetails,coinPrice}){
    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage ImgURL={ImgURL}/>
                <CoinName CoinTitle={CoinTitle}/> 
            </div>
            <CoinDetailInfo coinDetails={coinDetails}/>
            <CoinPriceInfo coinPrice={coinPrice}/>
            <button type="button" className='button' onClick={()=>{
                alert("Hey there !")
            }}>See more</button>
        </div>
    );
}

export default Coin;
