import React,{Component} from "react";
import { Line } from 'react-chartjs-2';

const returnData=()=>{
    var arr = [];
    while(arr.length < 6){
        var r = Math.floor(Math.random() * 10000) + 1;
        arr.push(r);
    }
    console.log(arr)
    return arr;
}

const labels = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
];

const data = {
  labels: labels,
  datasets: [{
    label: 'Ethereum',
    backgroundColor: 'rgb(255, 99, 132)',
    borderColor: 'rgb(255, 99, 132)',
    data: returnData(),
  }]
};

const options = {
    plugins: {
        legend: {
        display: false
        }
    },
    elements: {
        line: {
            tension: 0.5
        }
    },    
};

class Chart extends Component{

    render(){
        return (
            <>
                <div className='header'>
                <h1 className='title'>Price</h1>
                </div>
                <Line data={data} options={options} />
            </>
        );
    }
}

export default Chart;
