import React,{useState,useEffect} from 'react'
import CoinImage from './CoinImage'
import CoinName from './CoinName'
import CoinDetailInfo from './CoinDetailInfo';
import CoinPriceInfo from './CoinPriceInfo';

import './Coin.css'
import {fetchPriceData,options} from '../Data/data'
import {convertToDate} from '../Utils/utils'
import {BeatLoader} from 'react-spinners'

function Coin({Id,ImgURL,CoinTitle,coinDetails,coinPrice,removeCoin}){

    const [isLoading,setLoading]=useState(true); 
    const [priceData,setPriceData]=useState(fetchPriceData()); 
    const [isError,setError]=useState(false); 

    const getPriceData = async () => {
        const priceData = await fetchPriceData();
        setPriceData(priceData);
    };

    const updatePriceData=()=>{
        const URL="https://api.coingecko.com/api/v3/coins/bitcoin/market_chart/range?vs_currency=usd&from=1631553575&to=1634113153";
        fetch(URL)
            .then((resp)=>{
                if(resp.status>=200 && resp.status<=299){
                    return resp.json();
                }
            }).then((result)=>{
                const timeStampArr = result["prices"].map((price)=>{
                    return convertToDate(price[0]);
                })
                var priceDataArr = result["prices"].map((price)=>{
                    return Math.round(price[1]);
                })
                const data = {
                    labels: timeStampArr,
                    datasets: [{
                        label: 'Ethereum',
                        backgroundColor: 'rgb(0, 128, 0)',
                        borderColor: 'rgb(0, 128, 0)',
                        data: priceDataArr,
                    }]
                }
                setPriceData(data);
            }).catch((Error)=>{
                console.log(Error);
                setError(true);
            });   
    }

    useEffect(()=>{
        getPriceData()
            .then(()=>{
                setLoading(false);
            }).catch((error)=>{
                console.log(error)
                setError(true);
            });
    },[]);

    if(isLoading){
        return(
            <div className="Coin">
                <BeatLoader size={24} loading color='hsl(205, 78%, 60%)'/>
            </div>
        );
    }else if(isError){
        return(
            <div className="Coin">
                <h3> Something Went Wrong ! </h3>
            </div>
        );
    }

    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage ImgURL={ImgURL}/>
                <CoinName CoinTitle={CoinTitle}/> 
            </div>
            <CoinDetailInfo coinDetails={coinDetails}/>
            <CoinPriceInfo coinPrice={coinPrice} data={priceData} options={options}/>
            <button type="button" className='btn btn-refresh' onClick={()=>{
                updatePriceData();
            }}> Refresh </button>
            <button type="button" className='btn btn-delete' onClick={()=>{
                removeCoin(Id);
            }}> Delete </button>
        </div>
    );
}

export default Coin;
