import React from 'react'
import ReactDOM from 'react-dom'
import'./index.css'

function CoinImage(){
    return (
        <img  src="https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/BTC_Logo.svg/1200px-BTC_Logo.svg.png" alt="Img not Available" height="25px">
        </img>
    );
}

function CoinName(){
    const CoinTitle='BitCoin';
    return (
        <>
        <h2 style={{ display:'inline-block',verticalAlign:'bottom'}}>
            ${CoinTitle}
        </h2>
        </>
    );
}

function CoinDetailInfo(){
    return (
        <>
        <p>A cryptocurrency, crypto-currency, or crypto is a collection of binary data which is designed to work as a medium of exchange wherein individual coin ownership records are stored in a ledger which is a computerized database using strong cryptography to secure transaction records, to control the creation of additional coins, and to verify the transfer of coin ownership.</p>
        </>
    );
}

function CoinPriceInfo(){
    return (
        <>
        <h3 className='Coin-price'>$25,010/-</h3>
        </>
    );
}

function Coin(){
    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage/>
                <CoinName/> 
            </div>
            <CoinDetailInfo/>
            <CoinPriceInfo/>
        </div>
    );
}


const CoinList=()=>{
    return (
        <>
        <section className='CoinList'>
            <Coin/>
            <Coin/>
            <Coin/>
            <Coin/>
        </section>
        </>
    );

}

ReactDOM.render(
    <>
    <CoinList/>
    </>
    ,document.getElementById('root'));
