import React from 'react'
import Chart from '../Graphs/chart';

import './CoinPriceInfo.css'

const returnPriceData=()=>{
    var priceData = [];

    while(priceData.length < 6){
        var r = Math.floor(Math.random() * 10000) + 1;
        priceData.push(r);
    }
    console.log(priceData)

    const data = {
        labels: labels,
        datasets: [{
            label: 'Ethereum',
            backgroundColor: 'rgb(0, 128, 0)',
            borderColor: 'rgb(0, 128, 0)',
            data: priceData,
        }]
    }
    return data;
};

const labels = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
];


const options = {
    plugins: {
        legend: {
        display: false
        }
    },
    elements: {
        line: {
            tension: 0.5
        }
    },
    scales: {
    x: {
        legend: {
            display: false
        },
        grid:{
        display:false
        }
    },
    y: {
        legend: {
            display: false
        },
        grid:{
            display:false
        }
    }
    },

};

function CoinPriceInfo({coinPrice}){
    return (
        <>
        <Chart data={returnPriceData()} options={options}/>
        <h3 className='Coin-price'>$ {coinPrice} /-</h3>
        </>
    );
}


export default CoinPriceInfo;
