import React from 'react'
import ReactDOM from 'react-dom'
import'./index.css'

var Component = React.Component;
var CanvasJSReact = require('./canvasjs.react');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const options = {
			animationEnabled: true,
			title:{
				text: "Monthly Sales - 2017"
			},
			axisX: {
				valueFormatString: "MMM"
			},
			axisY: {
				title: "Sales (in USD)",
				prefix: "$"
			},
			data: [{
				yValueFormatString: "$#,###",
				xValueFormatString: "MMMM",
				type: "spline",
				dataPoints: [
					{ x: new Date(2017, 0), y: 25060 },
					{ x: new Date(2017, 1), y: 27980 },
					{ x: new Date(2017, 2), y: 42800 },
					{ x: new Date(2017, 3), y: 32400 },
					{ x: new Date(2017, 4), y: 35260 },
					{ x: new Date(2017, 5), y: 33900 },
					{ x: new Date(2017, 6), y: 40000 },
					{ x: new Date(2017, 7), y: 52500 },
					{ x: new Date(2017, 8), y: 32300 },
					{ x: new Date(2017, 9), y: 42000 },
					{ x: new Date(2017, 10), y: 37160 },
					{ x: new Date(2017, 11), y: 38400 }
				]
			}]
		}

/* const coinBitCoin={
 ImgURL :"https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/BTC_Logo.svg/1200px-BTC_Logo.svg.png",
 CoinTitle:'BitCoin',
 coinDetails:'A cryptocurrency, crypto-currency, or crypto is a collection of binary data which is designed to work as a medium of exchange wherein individual coin ownership records are stored in a ledger which is a computerized database using strong cryptography to secure transaction records, to control the creation of additional coins, and to verify the transfer of coin ownership.',
 coinPrice:25010,
}; */

const coinEthereum={
 ImgURL :"https://assets.gadgets360cdn.com/img/crypto/ethereum-og-logo.png",
 CoinTitle:'Ethereum',
 coinDetails:'Ethereum is a decentralized, open-source blockchain with smart contract functionality. Ether is the native cryptocurrency of the platform. Amongst cryptocurrencies, Ether is second only to Bitcoin in market capitalization. Ethereum was conceived in 2013 by programmer Vitalik Buterin.',
 coinPrice:3597.53,
};

function CoinImage({ImgURL}){
    return (
        <img className="coin-img"  src={ImgURL} alt="Img not Available" >
        </img>
    );
}

function CoinName({CoinTitle}){
    return (
        <>
        <h2 style={{ display:'inline-block',verticalAlign:'bottom'}}>
            {CoinTitle}
        </h2>
        </>
    );
}

function CoinDetailInfo({coinDetails}){
    return (
        <>
        <p>{coinDetails}</p>
        </>
    );
}

function CoinPriceInfo({coinPrice}){
    return (
        <>
        <h3 className='Coin-price'>$ {coinPrice} /-</h3>
        </>
    );
}

function Coin({ImgURL,CoinTitle,coinDetails,coinPrice}){
    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage ImgURL={ImgURL}/>
                <CoinName CoinTitle={CoinTitle}/> 
            </div>
            <CoinDetailInfo coinDetails={coinDetails}/>
            <CoinPriceInfo coinPrice={coinPrice}/>
        </div>
    );
}


const CoinList=({cryptoCoin})=>{
    return (
        <>
        <section className='CoinList'>
            <Coin ImgURL={cryptoCoin.ImgURL} 
                    CoinTitle={cryptoCoin.CoinTitle} 
                    coinDetails={cryptoCoin.coinDetails} 
                    coinPrice={cryptoCoin.coinPrice}/>
            <Coin ImgURL={cryptoCoin.ImgURL} 
                    CoinTitle={cryptoCoin.CoinTitle} 
                    coinDetails={cryptoCoin.coinDetails} 
                    coinPrice={cryptoCoin.coinPrice}/>
            <Coin ImgURL={cryptoCoin.ImgURL} 
                    CoinTitle={cryptoCoin.CoinTitle} 
                    coinDetails={cryptoCoin.coinDetails} 
                    coinPrice={cryptoCoin.coinPrice}/>
        </section>
        </>
    );
}

ReactDOM.render(
    <>
    <CoinList cryptoCoin={coinEthereum}/>
    <div>
			<CanvasJSChart options = {options}
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
    </>
    ,document.getElementById('root'));
