import React,{Component} from "react";
import { Line } from 'react-chartjs-2';

const data = {
  labels: ['1', '2', '3', '4', '5', '6'],
  datasets: [
    {
      label: 'Value of each coin',
      data: [5023, 9011, 1002, 5232, 7012, 666],
      fill: false,
      backgroundColor: 'rgb(0, 128, 0)',
      borderColor: 'rgba(0, 100, 0, 0.2)',
    },
  ],
};

const options = {
    elements: {
        line: {
            tension: 0.3
        }
    },
     scales: {
    x: {
        grid:{
        display:false
            }
        },
    y: 
        {
    grid:{
        display:false
            }
        }
    },
    legend: {
        display: false
        },
        tooltips: {
        enabled: false
    },
};

class Chart extends Component{

    render(){
        return (
            <>
                <div className='header'>
                <h1 className='title'>Price</h1>
                </div>
                <Line data={data} options={options} />
            </>
        );
    }
}

export default Chart;
