import React from 'react'
import ReactDOM from 'react-dom'
import'./index.css'

const coinBitCoin={
 ImgURL :"https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/BTC_Logo.svg/1200px-BTC_Logo.svg.png",
 CoinTitle:'BitCoin',
 coinDetails:'A cryptocurrency, crypto-currency, or crypto is a collection of binary data which is designed to work as a medium of exchange wherein individual coin ownership records are stored in a ledger which is a computerized database using strong cryptography to secure transaction records, to control the creation of additional coins, and to verify the transfer of coin ownership.',
 coinPrice:25010,
};

const ImgURL ="https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/BTC_Logo.svg/1200px-BTC_Logo.svg.png";
const CoinTitle='BitCoin';
const coinDetails='A cryptocurrency, crypto-currency, or crypto is a collection of binary data which is designed to work as a medium of exchange wherein individual coin ownership records are stored in a ledger which is a computerized database using strong cryptography to secure transaction records, to control the creation of additional coins, and to verify the transfer of coin ownership.';
const coinPrice=25010;

function CoinImage(){
    return (
        <img  src={ImgURL} alt="Img not Available" height="25px">
        </img>
    );
}

function CoinName(){
    return (
        <>
        <h2 style={{ display:'inline-block',verticalAlign:'bottom'}}>
            {CoinTitle}
        </h2>
        </>
    );
}

function CoinDetailInfo(){
    return (
        <>
        <p>{coinDetails}</p>
        </>
    );
}

function CoinPriceInfo(){
    return (
        <>
        <h3 className='Coin-price'>$ {coinPrice} /-</h3>
        </>
    );
}

function Coin(props){
    console.log(props);
    return (
        <div className="Coin">
            <div className="Coin-heading">
                <CoinImage props={coinBitCoin}/>
                <CoinName/> 
            </div>
            <CoinDetailInfo/>
            <CoinPriceInfo/>
        </div>
    );
}


const CoinList=()=>{
    return (
        <>
        <section className='CoinList'>
            <Coin />
            <Coin/>
            <Coin/>
            <Coin/>
        </section>
        </>
    );

}

ReactDOM.render(
    <>
    <CoinList/>
    </>
    ,document.getElementById('root'));
