import React from 'react'
import Chart from '../Graphs/chart';

import './CoinPriceInfo.css'

const options = {
    plugins: {
        legend: {
        display: false
        }
    },
    elements: {
        line: {
            tension: 0.25
        }
    },
    scales: {
    x: {
        legend: {
            display: false
        },
        grid:{
        display:false
        }
    },
    y: {
        legend: {
            display: false
        },
        grid:{
            display:false
        }
    }
    },

};

function CoinPriceInfo({coinPrice,data}){


    return (
        <>
        <Chart data={data} options={options}/>
        <h3 className='Coin-price'>$ {coinPrice} /-</h3>
        </>
    );
}


export default CoinPriceInfo;
